import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import avatar from '../assets/User.png';
import toast, { Toaster } from 'react-hot-toast';
import { useFormik } from 'formik';
import{ registerValidation } from '../helper/validate';
import convertToBase64 from '../helper/convert';
import { registerUser } from '../helper/helper';
import Header from './Header';


import styles from '../styles/UserName.module.css';


export default function Register() {

    const navigate = useNavigate()
    const [file, setFile] = useState()

    const formik = useFormik({
        initialValues : {
            email : 'artgagarin2@gmail.com',
            username: 'example123',
            password : 'admin@123'
        },
        validate : registerValidation,
        validateOnBlur: false,
        validateOnChange: false,
        onSubmit : async values => {
            values = await Object.assign(values, {profile : file || ''})
            // console.log(values)
            let registerPromise = registerUser(values)
            toast.promise(registerPromise, {
                loading: 'Создание...',
                success : <b>Регистрация успешна </b>,
                error : <b>Вы не зарегистрировались </b>
            });

            registerPromise.then(function(){ navigate('/Username')});
        }
    })

// не поддержтвается файл

const onUpload = async e => {
    const base64 = await convertToBase64(e.target.files[0]);
    setFile(base64);
}

    return (
        <div>
            <Header />
        <div className="container mx-auto padding-top-20vh">

            <Toaster position='top-center' reverseOrder={false}></Toaster>

            <div className='flex justify-center items-center h-95vh'>
                <div className={styles.glass} style={{width: "45%", passingTop: '3rem'}}>

                    <div className="title flex flex-col items-center">
                        <h4 className='text-5xl fotn-bold'>Регистрация</h4>
                        <span className='py-4 text-xl w-2/3 text-center text-gray-500'>
                            Добро пожаловать.
                        </span>
                    </div>

                    <form className='py-1' onSubmit={formik.handleSubmit}>
                    <div className='profile flex justify-center py-4'>
                  <label htmlFor="profile">
                  <img src={file || avatar} className={styles.profile_img} alt="avatar" />
                  </label>

                  <input onChange={onUpload} type="file" id='profile' name='profile' />
              </div>

                <div className="textbox flex flex-col items-center gap-6">
                  <input {...formik.getFieldProps('email')} className={styles.textbox} type="text" placeholder='Email' />
                  <input {...formik.getFieldProps('username')} className={styles.textbox} type="text" placeholder='Username' />
                  <input {...formik.getFieldProps('password')} className={styles.textbox} type="text" placeholder='Password' />
                  <button className={styles.btn} type='submit'>Зарегистрироваться</button>
              </div>

                           <div className="text-center py-4">
                            <span className='text-gray-500'>Уже зарегистрировались? <Link className='text-red-500' to='/Username'>Войти</Link></span>
                           </div>
                    </form>

                </div>
            </div>
        </div>
        </div>
    )
}
