import React, { useEffect, useState } from 'react';
import toast, { Toaster } from 'react-hot-toast';
import { useAuthStore } from '../store/store';
import { generateOTP, verifyOTP } from '../helper/helper';
import { useNavigate } from 'react-router-dom';


import styles from '../styles/UserName.module.css';


function validateOTPInput(username, code) {
    if (!username || typeof username !== 'string' || username.trim() === '') {
        console.error("Invalid username:", username);
        return false;
    }
    if (!code || typeof code !== 'string' || code.trim() === '') {
        console.error("Invalid OTP code:", code);
        return false;
    }
    return true;
}

export default function Recovery() {
    const { username } = useAuthStore(state => state.auth);
    const [OTP, setOTP] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchOTP = async () => {
            try {
                const otp = await generateOTP(username);
                console.log("Generated OTP:", otp);
                if (otp) {
                    toast.success('Код был выслан на вашу почту');
                } else {
                    toast.error('Возникла проблема при генерации вашего кода');
                }
            } catch (error) {
                toast.error('Произошла ошибка при отправке кода');
                console.error("Error generating OTP:", error);
            }
        };
        if (username) {
            fetchOTP();
        } else {
            toast.error('Имя пользователя отсутствует');
        }
    }, [username]);

    async function onSubmit(e) {
        e.preventDefault();
        console.log("Current OTP value:", OTP); // Добавлено логирование для проверки значения OTP
        if (!validateOTPInput(username, OTP)) {
            toast.error('Некорректные данные для проверки');
            return;
        }
        try {
            console.log("Verifying OTP with:", { username, code: OTP });
            let { status } = await verifyOTP({ username, code: OTP });
            if (status === 201) {
                toast.success('Проверка успешна');
                navigate('/reset');
            } else {
                toast.error('Код не правильный, проверьте свою почту');
            }
        } catch (error) {
            console.error("Error verifying OTP:", error);
            toast.error('Код не правильный, проверьте свою почту');
        }
    }
    

    //ручная отправка
    function resendOTP(){
        let sendPromise = generateOTP(username);

        toast.promise(sendPromise, {
            loading : 'Отправка',
            success : <b>Код был отправлен на вашу почту</b>,
            error : <b>Не получилось отправить</b>
        });

        sendPromise.then(OTP => {
            console.log(OTP)
        })
    }

    return (
        <div className="container mx-auto">

            <Toaster position='top-center' reverseOrder={false}></Toaster>

            <div className='flex justify-center items-center h-screen'>
                <div className={styles.glass}>
                    <div className="title flex flex-col items-center">
                        <h4 className='text-5xl fotn-bold'>Восстановление пароля</h4>
                        <span className='py-4 text-xl w-2/3 text-center text-gray-500'>
                            Введите код для смены пароля.
                        </span>
                    </div>

                    <form className='pt-20' onSubmit={onSubmit}>


                <div className="textbox flex flex-col items-center gap-6">

                    <div className="input text-center">
                    <span className='py-4 text-sm text-left text-gray-500'>
                        Ведите 6 значный код
                    </span>
                    <input onChange={(e) => setOTP(e.target.value) } className={styles.textbox} type="text" placeholder='OTP' />
                  </div>

                  <button className={styles.btn} type='submit'>Восстановить пароль</button>
              </div>

                           <div className="text-center py-4">
                            <span className='text-gray-500'>Код не пришел? <button className='text-red-500'>отправить снова</button></span>
                           </div>
                    </form>

                </div>
            </div>
        </div>
    )
}
