import React from 'react';
import '../styles/main.css';
import image from '../img/main.png';

const MainContent = () => {
    return (
        <main className="main-content">
            <img src={image} alt="Building Image" className="main-image" />
            <p className="main-text">
                Всё началось в тот солнечный день, когда я, прогуливаясь по узким улочкам старого города, наткнулся на заброшенное здание с потрясающей аркой. Меня охватило вдохновение, и я понял: хочу создать нечто, что оживит такие уголки, придаст им новую жизнь. Так родилась идея моей архитектурной мастерской.
            </p>
            <style jsx>{`
                @media (max-width: 768px) {
    .navbar {
        display: none;
        flex-direction: column;
        align-items: center;
        gap: 1rem;
        position: absolute;
        top: 60px;
        left: 0;
        width: 100%;
        background-color: white;
        border-top: 1px solid #ccc;
        padding: 1rem;
    }

    .navbar.active {
        display: flex;
    }

    .navbar a {
        font-size: 1rem;
    }

    .menu-toggle {
        display: block;
    }

    .main-content {
        padding: 1rem;
    }

    .main-text {
        font-size: 1rem;
    }

    .user-menu {
        position: absolute;
        right: 1rem;
        top: 1rem;
    }
}

@media (min-width: 769px) {
    .logo {
        display: flex;
        align-items: center;
        gap: 1rem;
    }

    .navbar {
        flex: 1;
        display: flex;
        padding-left: 1rem;
    }
}

@media (max-width: 480px) {
    .header {
        flex-direction: row;
        align-items: center;
        text-align: center;
    }

    .navbar {
        gap: 0.5rem;
    }

    .navbar a {
        font-size: 0.9rem;
    }

    .user-icon {
        font-size: 1.2rem;
    }

    .main-content {
        padding: 0.5rem;
    }

    .main-text {
        font-size: 0.9rem;
    }

    .footer p {
        font-size: 0.8rem;
    }
}

            `}</style>
        </main>
    );
};

export default MainContent;
