import toast from 'react-hot-toast'

// validate login page username

export async function usernameValidate(values){
    const errors = usernameVerify({}, values);

    return errors;
}

// validate password

export async function passwordValidate(values){
    const errors = passwordVerify({}, values);

    return errors;
}

// validate password
function passwordVerify(errors = {}, values){

    const specialChars = /[`!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/;
    
    if (!values.password){
        errors.password = toast.error(" Требуется пароль!")
    }else if(values.password.includes(" ")){
        error.password = toast.error('Не правильный пароль!')
    }else if(values.password.lenght < 4){
        error.password = toast.error('Пароль должен быть длинее 4 символов')
    }else if(!specialChars.test(values.password)){
        errors.password = toasr.error("Пароль должен содержать специальные символы")
    }

    return errors;
}

// validate username
function usernameVerify(error = {}, values){
    if(!values.username){
        error.username = toast.error('Требуется имя пользователя!');
    }else if(values.username.includes(" ")){
        error.username = toast.error('Неверное имя пользователя!')
    }

    return error;
}
